mapper.select:
select * from t_user
where user_pwd = #{userPwd}
and (email=#{email} or phone_num=#{phoneNum})

mapper.update:
update t_user
set user_name=#{userName}, nickname=#{nickname},
    stu_num=#{stuNum}, gender=#{gender},
    birthday=#{birthday}, signature=#{signature}
where user_id = #{userId}

update t_user
set user_pwd = #{userPwd}
where email = #{email}

update t_user
set email = #{email}
where user_id = #{userId}

update t_user
set phone_num = #{phoneNum}
where user_id = #{userId}
